import { Component } from '@angular/core';

@Component({
  selector: 'app-heroes-hero',
  templateUrl: './hero.component.html',
  styleUrls: ['./hero.component.css']
})
export class HeroComponent {
   nombre:string = "ironMan";
   edad:number = 45;

   get capitalizedName():string{
      return "Hola Mundo";
   }
}
